        /// <summary>
        /// { OP.PushData1, 3, 65, 82, 174 }
        /// </summary>
        private static readonly byte[] BadEncoding = new byte[] { (byte)OP.PushData1, 3, 65, 82, 174 };
        /// <summary>
        /// { 65, 82, 174 }
        /// </summary>
        private static readonly byte[] BadEncodingData = new byte[] { 65, 82, 174 };
        private static PushDataOp GetBadEncodingOp()
        {
            PushDataOp result = new();
            Assert.True(result.TryRead(new FastStreamReader(BadEncoding), out Errors error), error.Convert());
            return result;
        }

        public static IEnumerable<object[]> GetRunCases()
        {
            yield return new object[] { new PushDataOp(0L), Array.Empty<byte>() };
            yield return new object[] { new PushDataOp(-1), new byte[] { 0b10000001 } };
            yield return new object[] { new PushDataOp(1), new byte[] { 1 } };
            yield return new object[] { new PushDataOp(2), new byte[] { 2 } };
            yield return new object[] { new PushDataOp(16), new byte[] { 16 } };
            yield return new object[] { new PushDataOp(new byte[] { 1, 2, 3 }), new byte[] { 1, 2, 3 } };
            // Edge case where the StackInt is not correctly used. Has to only push the data.
            yield return new object[] { GetBadEncodingOp(), BadEncodingData };
        }
        [Theory]
        [MemberData(nameof(GetRunCases))]
        public void RunTest(PushDataOp op, byte[] expectedData)
        {
            MockOpData opData = new(FuncCallName.Push)
            {
                _itemCount = 0,
                _altItemCount = 0,
                pushData = new byte[][] { expectedData }
            };

            bool b = op.Run(opData, out Errors error);

            // The mock data is already checking the call type and the data that was pushed to be correct.
            Assert.True(b, error.Convert());
            Assert.Equal(Errors.None, error);
        }

        [Fact]
        public void Run_ItemCountOverflowTest()
        {
            PushDataOp op = new(OpTestCaseHelper.b1);
            MockOpData opData = new(FuncCallName.Push)
            {
                _itemCount = 1001,
                _altItemCount = 0,
                pushData = new byte[][] { OpTestCaseHelper.b1 }
            };

            bool b = op.Run(opData, out Errors error);

            Assert.False(b);
            Assert.Equal(Errors.StackItemCountOverflow, error);
        }

        [Fact]
        public void Run_ItemLenghOverflowTest()
        {
            PushDataOp op = new();
            byte[] data = Helper.HexToBytes($"4d0902{Helper.GetBytesHex(521)}");
            bool b1 = op.TryRead(new FastStreamReader(data), out Errors error);
            Assert.True(b1, error.Convert());
            MockOpData opData = new();

            bool b2 = op.Run(opData, out Errors err);

            Assert.False(b2);
            Assert.Equal(Errors.StackPushSizeOverflow, err);
        }


