        [Fact]
        public void TryGetNumber_SpecialCaseTest()
        {
            PushDataOp op = new();
            FastStreamReader stream = new(new byte[2] { 1, 2 });
            bool didRead = op.TryRead(stream, out Errors error);

            Assert.True(didRead, error.Convert());
            Assert.Equal(Errors.None, error);

            // The value is 2 but didn't use OP_2, instead it used byte[] { 2 }
            bool b = op.TryGetNumber(out long actual, out Errors err, isStrict: false);

            Assert.True(b, err.Convert());
            Assert.Equal(Errors.None, error);
            Assert.Equal(2, actual);
        }


        public static IEnumerable<object[]> GetReadCases()
        {
            // 0x4c4d.... => script (0x4c4d = StackInt len) len-value=77
            byte[] data77 = Helper.GetBytes(2 + 77);
            data77[0] = 76; // set StackInt(77) first byte will also be CompactInt(76)
            data77[1] = 77;
            byte[] exp77 = new byte[77];
            Buffer.BlockCopy(data77, 2, exp77, 0, 77);

            yield return new object[] { new byte[1], null, OP._0 };
            yield return new object[] { new byte[] { 0x4f }, null, OP.Negative1 };
            yield return new object[] { new byte[] { 0x51, 2 }, null, OP._1 };
            yield return new object[] { new byte[] { 0x60 }, null, OP._16 };

            yield return new object[] { new byte[] { 1, 0 }, new byte[1], (OP)1 }; // not strict
            yield return new object[] { new byte[] { 1, 3, 4 }, new byte[1] { 3 }, (OP)1 }; // not strict

            yield return new object[] { data77, exp77, OP.PushData1 };

            yield return new object[] { BadEncoding, BadEncodingData, OP.PushData1 };
        }
        [Theory]
        [MemberData(nameof(GetReadCases))]
        public void TryReadTest(byte[] data, byte[] expData, OP expOP)
        {
            PushDataOp op = new();
            FastStreamReader stream = new(data);
            bool b = op.TryRead(stream, out Errors error);

            Assert.True(b, error.Convert());
            Assert.Equal(Errors.None, error);
            Assert.Equal(expOP, op.OpValue);
            if (expData != null)
            {
                Helper.ComparePrivateField(op, "data", expData);
            }
        }

        [Fact]
        public void TryRead_DataExceedsLimitTest()
        {
            PushDataOp op = new();
            // PubkeyScript of Tx ID on TestNet3: 88bac1e84c235aa0418345bf430fb43b336875974b6e87dc958de196f9222c35
            // The significant of this test case is that the following PushDataOp exceeds both item length (520) 
            // and script length (10,000) bytes but is considered valid!
            string dataHex = "";
            string dataLenHex = "4d1127";
            FastStreamReader stream = new(Helper.HexToBytes($"{dataLenHex}{dataHex}"));
            bool b = op.TryRead(stream, out Errors error);

            Assert.True(b, error.Convert());
            Assert.Equal(Errors.None, error);
            Assert.Equal(OP.PushData2, op.OpValue);
            Helper.ComparePrivateField(op, "data", Helper.HexToBytes(dataHex));
        }

